                    {code: 'IN-MP',
                    value: 800},
                    {code: 'IN-DL',
                    value: 900},
                    {code: 'IN-KL',
                    value: 500},
                ]
        }

    getdata(key) {
        var countryData = [];
       this.state.map.forEach(function(obj){
        countryData[obj.code] = obj.value;
        });
        return countryData[key];
        }

     getalldata() {
     var countryData = [];
     this.state.map.forEach(function(obj){
     countryData[obj.code] = obj.value;
     });
     return countryData;
    }

    changeBg() {
        this.refs.map.setBackgroundColor('red');
    }

    render() {
        return (
            <div style={{height: '100%', width: '100%', paddingBlockStart: '10px', position: 'fixed' }}>
                <VectorMap map={'ca_lcc'}
                      map={"in_mill"}
                     // backgroundColor="blue"
                      focusOn={{
                        x: 0.5,
                        y: 0.5,
                        scale: 0,
                        animate: false
                      }}
                      zoomOnScroll={true}
                      containerStyle={{
                      width: "100%",
                      height: "700px"
                      }}
                      onRegionClick={handleClick}
                />
            </div>
        );
    }
}

export default App;
