import React from "react";
import { toast, ToastContainer } from 'react-toastify';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';

class TotalCase extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            total: '',
            date: '',
            totalConfirmeCase: '',
            totalActiveCase: '',
            totalRecoveredCase: '',
            totalDeathCase: ''

        }
    }

    async componentDidMount() {
        await axios.get('https://api.covid19india.org/data.json')
            .then(response => {
             //   toast.info("success")
                this.setState({ total: response.data.statewise[0] });

            }).catch(err => {
                toast.error("Internal Server Error")
            })

        this.setState({ totalConfirmeCase: this.state.total.confirmed });
        this.setState({ totalActiveCase: this.state.total.active });
        this.setState({ totalRecoveredCase: this.state.total.recovered });
        this.setState({ totalDeathCase: this.state.total.deaths });
        this.setState({ date: this.state.total.lastupdatedtime });

    }

    render() {
        const { classes, theme, isLoggedIn, openSidebar } = this.props;

        return (
            <div className="App">
                <ToastContainer />
                <Paper elevation={9} style={{ padding: '10px', borderRadius: '15px', width: '60%',/* background: 'linear-gradient(to bottom left,#FC3409 10% ,#F8E4E2 80%)'*/ }} >

                    <h4 style={{ color: '#85929E',textAlign: "center",fontSize:"10%" }}> Record Date {this.state.date}</h4>
                    <h4 style={{fontSize:"1vw"}}>  Total Confirme Cases:- {this.state.totalConfirmeCase}</h4>
                    <h4 style={{/* color: 'orange'*/ fontSize:"1vw"}}> Total Active Case:-   {this.state.totalActiveCase}</h4>
                    <h4 style={{ color: 'green',fontSize:"1vw" }}>Total Recovered Case:-  {this.state.totalRecoveredCase}</h4>
                    <h4 style={{ color: 'red',fontSize:"1vw" }}>Total Death Case:-        {this.state.totalDeathCase}</h4>

                </Paper>
            </div>
        )
    }
}
export default TotalCase;
