import React from "react";
import { VectorMap } from "react-jvectormap";
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const handleClick = (event, code) => {
    console.log(`You have click ${code}`)
}
class App extends React.Component {

        state = {   
            map: [
                   {code: 'IN-RJ',
                    value: 10000},
                    {code: 'IN-MP',
                    value: 800},
                    {code: 'IN-DL',
                    value: 900},
                    {code: 'IN-KL',
                    value: 500},
                ],
                indiaState:[],
        }

    getdata(key) {
        var countryData = [];
       this.state.map.forEach(function(obj){
        countryData[obj.code] = obj.value;
        });
        return countryData[key];
        }

     getalldata() {
     var countryData = [];
     this.state.map.forEach(function(obj){
     countryData[obj.code] = obj.value;
     });
     return countryData;
    }

    changeBg() {
        this.refs.map.setBackgroundColor('red');
    }

    handleshow2 = (e, el, code) => {
        console.log("********************",code)
        var res = code.replace("IN-", "");

        el.html(el.html() + ` <br> Staticssssss: ${this.getdata(code)}`);
      };
      componentDidMount() {
        // const requestURL = 'api/ListOfChild'
        // const listofchild = call(get, { url: requestURL});

        axios.get('https://api.covid19india.org/data.json')
        .then(response => {
            console.log("response****",response)
            toast.info("success")
            this.setState({ indiaState: response.data.statewise}); 
            console.log("****",this.state.indiaState) 

        }).catch(err => {
          toast.error("Internal Server Error")
        })
    }

    render() {
        return (
            <div style={{height: '100%', width: '100%', paddingBlockStart: '10px', position: 'fixed' }}>
               <ToastContainer />
                <VectorMap map={'ca_lcc'}
                      map={"in_mill"}
                     // backgroundColor="blue"
                      focusOn={{
                        x: 0.5,
                        y: 0.5,
                        scale: 0,
                        animate: false
                      }}
                      zoomOnScroll={true}
                      containerStyle={{
                      width: "100%",
                      height: "700px"
                      }}
                      onRegionClick={handleClick} //gets the country                         code
                      onRegionTipShow={this.handleshow2}
                      containerClassName="map"
                      regionStyle={{
                        initial: {
                           fill: "#e4e4e4",
                           "fill-opacity": 0.9,
                           stroke: "none",
                           "stroke-width": 0,
                           "stroke-opacity": 0
                        },
                        hover: {
                           "fill-opacity": 0.8,
                           cursor: "pointer"
                        },
                        selected: {
                           fill: "#2938bc" // onclick colour of state
                        },
                      }}
                      regionsSelectable={false}
                      series={{
                         regions: [
                             {
                              values: this.getalldata(), //can be directly served //with api response or any data
                              scale: ['#C8EEFF', '#0071A4'], //color range
                              normalizeFunction: "polynomial"
                             }
                         ]
                      }}
                />
            </div>
        );
    }
}

export default App;
