import React from "react";
import { toast, ToastContainer } from 'react-toastify';
import axios from 'axios';

import Paper from '@material-ui/core/Paper';

class TotalCase extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            total:'',
            date: '',
            totalConfirmeCase: '',
            totalActiveCase: '',
            totalRecoveredCase: '',
            totalDeathCase: ''

        }
    }

   async componentDidMount() {
      // await console.log("***********totalData", this.props);

      await axios.get('https://api.covid19india.org/data.json')
       .then(response => {
           toast.info("success")
           this.setState({ total: response.data.statewise[0]}); 
           console.log("****55555",this.state.total) 

       }).catch(err => {
         toast.error("Internal Server Error")
       })
     //   if (this.props.total == "undefined") {
        //    toast.error("Internal Server Error")
    //    }
     //   else {
            this.setState({ totalConfirmeCase: this.state.total.confirmed });
            this.setState({ totalActiveCase: this.state.total.active });
            this.setState({ totalRecoveredCase: this.state.total.recovered });
            this.setState({ totalDeathCase: this.state.total.deaths });
            this.setState({ date: this.state.total.lastupdatedtime });

            console.log("***********totalData", this.state.total)
    //    }
    }

    render() {
        return (
            <div>
                <ToastContainer />

                <Paper elevation={9} style={{ padding: '20px', borderRadius: '15px', width: '60%',/* background: 'linear-gradient(to bottom left,#FC3409 10% ,#F8E4E2 80%)'*/ }} >

                    <h3 style={{ color: 'yellow' }}> Recored Date {this.state.date}</h3>
                    <h3 style={{ color: 'yellow' }}> India total Confirme Cases:- {this.state.totalConfirmeCase}</h3>
                    <h3 style={{ color: 'orange' }}> India total Active Case:-   {this.state.totalActiveCase}</h3>
                    <h3 style={{ color: 'green' }}>India total Recovered Case:-  {this.state.totalRecoveredCase}</h3>
                    <h3 style={{ color: 'red' }}>India total Death Case:-        {this.state.totalDeathCase}</h3>

                </Paper>
            </div>
        )
    }
}
export default TotalCase;
