import React from "react";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Paper from '@material-ui/core/Paper';

class detailed extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            districtName: '', 
            cityName: '', 
            countryData:[]
        }
    }
    handleChange = (event) => {
        var countryData = [];

        this.setState({ cityName: event.target.value });
        console.log("propssss",this.state.countryData)

      /*  this.props.data.forEach(function(obj){
            countryData[obj.] = obj;
            });
            return countryData[key];*/

            var data = Object.entries(this.getdata(event.target.value))
            console.log("districtData****",data)
    };
    getdata(key) {
        var countryData = [];
        console.log("key****",key)
       this.state.countryData.forEach(function(obj){
        console.log("obj[0]****",obj[0])
           if(key == obj[0]) { 
            console.log("true****",obj[1])
           countryData= obj[1].districtData;
           return countryData;
        }
        });
        return countryData;

        }

    componentDidMount() {
        this.setState({ countryData:  this.props.data });

    }

    render() {console.log("propssss update",localStorage.getItem("city"));
        return (
            <div>
                 <Paper elevation={3} style={{  padding: '10px',borderRadius: '15px', width: '60%'}} >
                 <InputLabel id="demo-simple-select-outlined">City Name  </InputLabel>
                <Select style={{  width: '80%', borderRadius: '20px', height: '50px' }}
                    labelId="demo-simple-select-outlined-label"
                    id="demo-simple-select-outlined"
                    value={this.state.cityName}
                    onChange={this.handleChange}
                    label="City Name"
                    variant="outlined"
                >  
               { 
               this.props.data.map((data, i) => (
                    <MenuItem value={data[0]}>{data[0]}</MenuItem>
               ))}
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>

                </Select><br/><br/>
                <InputLabel id="demo-simple-select-outlined">District Name  </InputLabel>
                <Select style={{  width: '80%', borderRadius: '20px', height: '50px' }}
                    labelId="demo-simple-select-outlined-label"
                    id="demo-simple-select-outlined"
                    value={this.state.age}
                    onChange={this.handleChange}
                    label="District Name"
                    variant="outlined"
                >
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value='Maharashtra'>Maharashtra</MenuItem>
                    <MenuItem value={20}>Twenty</MenuItem>
                    <MenuItem value={30}>Thirty</MenuItem>
                </Select><br/><br/>
                Confirme Cases:<br/>
                Active Case:<br/>
                Death Case:<br/>
                city   {localStorage.getItem("city")}
                <br/><br/><br/><br/>
                </Paper>
            </div>
        )
    }
}
export default detailed;
