import React from "react";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import Paper from '@material-ui/core/Paper';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import Typography from '@material-ui/core/Typography';

class detailed extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            districtName: '',
            cityName: '',
            countryData: [],
            districtData: [],
            confirmeCase: '',
            activeCase: '',
            recoveredCase: '',
            deathCase: ''

        }
    }
    handleChange = (event) => {
        this.setState({ cityName: event.target.value });
        this.setState({ districtData: Object.entries(this.getdata(event.target.value)) });
    };
    getdata(key) {
        var countryData = [];
        this.state.countryData.forEach(function (obj) {
            if (key == obj[0]) {
                countryData = obj[1].districtData;
                return countryData;
            }
        });
        return countryData;
    }
    handleChange1 = (event) => {
        var detailedInfo = [];
        this.setState({ districtName: event.target.value });
        this.state.districtData.forEach(function (obj) {
            if (event.target.value == obj[0]) {
                detailedInfo = obj[1];
                return detailedInfo;
            }
        });
        this.setState({ confirmeCase: detailedInfo.confirmed });
        this.setState({ activeCase: detailedInfo.active });
        this.setState({ recoveredCase: detailedInfo.recovered });
        this.setState({ deathCase: detailedInfo.deceased });
        return detailedInfo;
    };

    async componentDidMount() {
        await axios.get('https://api.covid19india.org/state_district_wise.json')
            .then(response => {
                var data = Object.entries(response.data)
                this.setState({ countryData: data }, () => {
                    console.log("****countryData////////////", this.state.countryData)
                });

            }).catch(err => {
                toast.error("Internal Server Error")
            })
    }

    render() {
        return (
            <div>
                <Paper elevation={9} style={{ padding: '15px', borderRadius: '15px', width: '60%',/* background: 'linear-gradient(to bottom left,#FC3409 10% ,#F8E4E2 80%)'*/ }} >
                    <InputLabel id="demo-simple-select-outlined">State Name  </InputLabel>
                    <Select style={{ width: '90%', borderRadius: '20px', height: '10%' }}
                        labelId="demo-simple-select-outlined-label"
                        id="demo-simple-select-outlined"
                        value={this.state.cityName}
                        onChange={this.handleChange}
                        //  label="City Name"
                        variant="outlined"
                    >
                        {
                            this.state.countryData.map((data, i) => (
                                <MenuItem value={data[0]}>{data[0]}</MenuItem>
                            ))}
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>

                    </Select><br /><br />
                    <InputLabel id="demo-simple-select-outlined">District Name  </InputLabel>
                    <Select style={{ width: '90%', borderRadius: '20px', height: '50px' }}
                        labelId="demo-simple-select-outlined"
                        id="demo-simple-select-outlined"
                        value={this.state.age}
                        onChange={this.handleChange1}
                      //    label="District Name"
                        variant="outlined"
                    >
                        {
                            this.state.districtData.map((data, i) => (
                                <MenuItem value={data[0]}>{data[0]}</MenuItem>
                            ))}
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>

                    </Select>
                    <h4 style={{ /*color: 'yellow' */fontSize:"1vw"}}> Confirme Cases: {this.state.confirmeCase}</h4>
                    <h4 style={{ /*color: 'orange'*/fontSize:"1vw" }}>  Active Case: {this.state.activeCase}</h4>
                    <h4 style={{ color: 'green', fontSize:"1vw" }}>Recovered Case: {this.state.recoveredCase}</h4>
                    <h4 style={{ color: 'red', fontSize:"1vw" }}>Death Case: {this.state.deathCase}</h4>
                </Paper>
            </div>
        )
    }
}
export default detailed;
