import React from "react";
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Detailed from './detailed';
import TotalCase from './TotalCase';
import Map from './map';
import Grid from '@material-ui/core/Grid';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams
} from "react-router-dom";

class App extends React.Component {

  render() {
    return (
      <div style={{ height: '100%', width: '100%',/* paddingBlockStart: '15px',*/ position: 'fixed',/* backgroundColor: "#FEF5E7",*/ backgroundImage: 'linear-gradient(to top, #137a8b 0%, #30e4fb 100%)' }}>
        <ToastContainer />
        <h2 style={{ color: '#85929E', textAlign: "center" }}>India covid update</h2>
        <br />
        <div>
          <Router>
            <Grid container
              direction="row"
              justify="flex-start"
              alignItems="flex-start"
              spacing={10}
            >
              <Grid item xs={7}>
                <Map />
              </Grid>
              <Grid item xs>
                <TotalCase />
                <ul>
                  <li>
                    <Link to="/detailed">State Wise Detailed Information </Link>
                  </li>
                </ul>

                <Switch>
                  <Route exact path="/detailed"><Detailed /></Route>
                </Switch>
              </Grid>
            </Grid>
          </Router>
        </div>
      </div>
    );
  }
}

export default App;
