import React from "react";

import Paper from '@material-ui/core/Paper';

class TotalCase extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            recoveredCase:'',
            totalConfirmeCase:'',
            totalActiveCase:'',
            totalRecoveredCase:'',
            totalDeathCase:''

        }
    }

    componentDidMount() {
        this.setState({ totalConfirmeCase: this.props.total.confirmed});
        this.setState({ totalActiveCase: this.props.total.active});
        this.setState({ totalRecoveredCase: this.props.total.recovered});
        this.setState({ totalDeathCase: this.props.total.deaths});
        console.log("***********totalData",this.props.total)

    }

    render() {
        return (
            <div>
                <Paper elevation={9} style={{ padding: '20px', borderRadius: '15px', width: '60%',/* background: 'linear-gradient(to bottom left,#FC3409 10% ,#F8E4E2 80%)'*/ }} >      
                    <br /><br /><br />
                    <h3 style={{ color: 'green' }}>Last Updated time: {this.props.total.lastupdatedtime}</h3>
                    <h3 style={{ color: 'yellow'}}> India total Confirme Cases: {this.state.totalConfirmeCase}</h3>
                    <h3 style={{ color: 'red' }}> India total Active Case: {this.state.totalActiveCase}</h3>
                    <h3 style={{ color: 'green' }}>India total Recovered Case: {this.state.totalRecoveredCase}</h3>
                    <h3 style={{ color: 'green' }}>India total Death Case: {this.state.totalDeathCase}</h3>

                    <br /><br /><br />
                </Paper>
            </div>
        )
    }
}
export default TotalCase;
