import React from "react";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Paper from '@material-ui/core/Paper';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';

class detailed extends React.Component {
    _isMounted = false;
    constructor(props) {
        super(props);
        this.state = {
            districtName: '',
            cityName: '',
            countryData: [],
            districtData: [],
            confirmeCase:'',
            activeCase:'',
            recoveredCase:'',
            deathCase:''

        }
    }
    handleChange = (event) => {
        var countryData = [];

        this.setState({ cityName: event.target.value });


        this.setState({ districtData: Object.entries(this.getdata(event.target.value)) });
    };
    getdata(key) {
        var countryData = [];
        this.props.data.forEach(function (obj) {
            if (key == obj[0]) {
                countryData = obj[1].districtData;
                return countryData;
            }
        });
        return countryData;

    }
    handleChange1 = (event) => {
        var detailedInfo = [];
        this.setState({ districtName: event.target.value });
        console.log("***********distric data",this.state.districtData)
        this.state.districtData.forEach(function (obj) {
            if (event.target.value == obj[0]) {
                detailedInfo = obj[1];
                //console.log("***********obj[1]",obj[1].confirmed)
                //this.setState({ confirmeCase: obj[1].confirmed });
               // this.setState({ activeCase: obj[1].active });
                //this.setState({ districtName: event.target.value });

                return detailedInfo;
            }
        });
        console.log("***********detailedInfo",detailedInfo)

        this.setState({ confirmeCase: detailedInfo.confirmed });
        this.setState({ activeCase: detailedInfo.active });
        this.setState({ recoveredCase: detailedInfo.recovered });
        this.setState({ deathCase: detailedInfo.deceased });


        return detailedInfo;

    };

    componentDidMount() {
        this._isMounted = true;
      /*  this.setState({ countryData: this.props.data }  , () => {
            console.log("***********countryData",this.state.countryData);});*/
       // console.log("***********props data",this.props.data)
        axios.get('https://api.covid19india.org/state_district_wise.json')
        .then(response => {
            console.log("state_district_wise****",response)
            toast.info("state_district_wise")
            this.setState({ countryData:Object.entries( response.data)}); 
            console.log("****countryData////////////",this.state.countryData) 

        }).catch(err => {
          toast.error("Internal Server Error")
        })

        this.state.countryData.map((data, i) => (

            console.log("data[0]****",data[0])
        ))


    }

    render() {
        return (
            <div>
                <Paper elevation={9} style={{ padding: '20px', borderRadius: '15px', width: '60%',/* background: 'linear-gradient(to bottom left,#FC3409 10% ,#F8E4E2 80%)'*/ }} >
                    <InputLabel id="demo-simple-select-outlined">State Name  </InputLabel>
                    <Select style={{ width: '80%', borderRadius: '20px', height: '50px' }}
                        labelId="demo-simple-select-outlined-label"
                        id="demo-simple-select-outlined"
                        value={this.state.cityName}
                        onChange={this.handleChange}
                        label="City Name"
                        variant="outlined"
                    >
                        {
                            this.state.countryData.map((data, i) => (
                                <MenuItem value={data[0]}>{data[0]}</MenuItem>
                            ))}
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>

                    </Select><br /><br />
                    <InputLabel id="demo-simple-select-outlined">District Name  </InputLabel>
                    <Select style={{ width: '80%', borderRadius: '20px', height: '50px' }}
                        labelId="demo-simple-select-outlined-label"
                        id="demo-simple-select-outlined"
                        value={this.state.age}
                        onChange={this.handleChange1}
                        label="District Name"
                        variant="outlined"
                    >
                        {
                            this.state.districtData.map((data, i) => (
                                <MenuItem value={data[0]}>{data[0]}</MenuItem>
                            ))}
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>
                        deathCase
                    </Select><br /><br />
                    <h3 style={{ color: 'yellow'}}> Confirme Cases: {this.state.confirmeCase}</h3>
                    <h3 style={{ color: 'orange' }}>  Active Case: {this.state.activeCase}</h3>
                    <h3 style={{ color: 'green' }}>Recovered Case: {this.state.recoveredCase}</h3>
                    <h3 style={{ color: 'red' }}>Death Case: {this.state.deathCase}</h3>

                </Paper>
            </div>
        )
    }
}
export default detailed;
