import React from "react";
import { VectorMap } from "react-jvectormap";
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const handleClick = (event, code) => {
    console.log(`You have click ${code}`)
}
class App extends React.Component {

        state = {   
            map: [
                ["1712", "IN-AN"],
                ["1822", "IN-AP"],
                ["3456", "IN-AR"],
                ["1927", "IN-AS"],
                ["2978", "IN-BR"],
                ["1931", "IN-CH"],
                ["2980", "IN-CT"],
                ["1937", "IN-DN"],
                ["3464", "IN-DD"],
                ["1932", "IN-DL"],
                ["2985", "IN-GA"],
                ["2984", "IN-GJ"],
                ["1934", "IN-HR"],
                ["1933", "IN-HP"],
                ["1948", "IN-JK"],
                ["2977", "IN-JH"],
                ["1938", "IN-KA"],
                ["1935", "IN-KL"],
                ["1714", "IN-LD"],
                ["2981", "IN-MP"],
                ["1939", "IN-MH"],
                ["1928", "IN-MN"],
                ["1930", "IN-ML"],
                ["3462", "IN-MZ"],
                ["1929", "IN-NL"],
                ["1936", "IN-OR"],
                ["2982", "IN-PY"],
                ["2973", "IN-PB"],
                ["2974", "IN-RJ"],
                ["2979", "IN-SK"],
                ["2983", "IN-TN"],
                ["3463", "IN-TR"],
                ["2975", "IN-UP"],
                ["2976", "IN-UT"],
                ["2891", "IN-WB"]
                ],
                indiaState:[],
        }

    getdata(key) {
        var countryData = [];
       this.state.indiaState.forEach(function(obj){
        countryData[obj.statecode] = obj;
        });
        return countryData[key];
        }

     getalldata() {
     var countryData = [];
     this.state.map.forEach(function(obj){
     countryData[obj.code] = obj.value;
     });
     return countryData;
    }

    changeBg() {
        this.refs.map.setBackgroundColor('red');
    }

    handleshow2 = (e, el, code) => {
        console.log("********************",code)
        var res = code.replace("IN-", "");

        el.html(el.html() + 
        `<br> confirmed cases: ${this.getdata(res).confirmed}
        <br> active cases: ${this.getdata(res).active}
        <br> deaths cases: ${this.getdata(res).deaths}`
         );
      };
      componentDidMount() {
        // const requestURL = 'api/ListOfChild'
        // const listofchild = call(get, { url: requestURL});

        axios.get('https://api.covid19india.org/data.json')
        .then(response => {
            console.log("response****",response)
            toast.info("success")
            this.setState({ indiaState: response.data.statewise}); 
            console.log("****",this.state.indiaState) 

        }).catch(err => {
          toast.error("Internal Server Error")
        })
    }

    render() {
        return (
            <div style={{height: '100%', width: '100%',/* paddingBlockStart: '15px',*/ position: 'fixed' }}>
               <ToastContainer />
             <h3  style={{align:"center"}}>  India covid update</h3>
                <VectorMap map={'ca_lcc'}
                      map={"in_mill"}
                      backgroundColor="white"
                      focusOn={{
                        x: 0.5,
                        y: 0.5,
                        scale: 0,
                        animate: false
                      }}
                      zoomOnScroll={true}
                      containerStyle={{
                      width: "100%",
                      height: "700px"
                      }}
                      onRegionClick={handleClick} //gets the country                         code
                      onRegionTipShow={this.handleshow2}
                      containerClassName="map"
                      regionStyle={{
                        initial: {
                           fill: "#e4e4e4",
                           "fill-opacity": 0.9,
                           stroke: "none",
                           "stroke-width": 0,
                           "stroke-opacity": 0
                        },
                        hover: {
                           "fill-opacity": 0.8,
                           cursor: "pointer"
                        },
                        selected: {
                            fill: 'yellow'
                          },
                      }}
                      regionsSelectable={false}
                      series={{
                         regions: [
                             {
                              values: this.getalldata(), //can be directly served //with api response or any data
                              scale: ['#C8EEFF', '#0071A4'], //color range
                              normalizeFunction: "polynomial"
                             }
                         ]
                      }}
                />
            </div>
        );
    }
}

export default App;
